  --popover-border: 225 6% 24%;
  --primary: 225 85% 15%;
  --primary-foreground: 0 0% 98%;
  --secondary: 225 6% 18%;
  --secondary-foreground: 0 0% 95%;
  --muted: 225 6% 14%;
  --muted-foreground: 0 0% 65%;
  --accent: 210 20% 16%;
  --accent-foreground: 210 100% 85%;
  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 98%;
  --input: 225 6% 24%;
  --ring: 210 100% 60%;
  --chart-1: 210 100% 85%;
  --chart-2: 210 100% 70%;
  --chart-3: 270 85% 75%;
  --chart-4: 180 85% 65%;
  --chart-5: 300 85% 70%;

  --shadow-2xs: 0px 2px 0px 0px hsl(225 6% 18% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(225 6% 18% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(225 6% 18% / 0.00), 0px 1px 2px -1px hsl(225 6% 18% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(225 6% 18% / 0.00), 0px 1px 2px -1px hsl(225 6% 18% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(225 6% 18% / 0.00), 0px 2px 4px -1px hsl(225 6% 18% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(225 6% 18% / 0.00), 0px 4px 6px -1px hsl(225 6% 18% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(225 6% 18% / 0.00), 0px 8px 10px -1px hsl(225 6% 18% / 0.00);
