  // Clean CollapsibleTableSection Component - NO OBJECT LITERALS IN JSX
  interface CollapsibleSectionProps {
    title: string;
    sectionKey: string;
    children: React.ReactNode;
    statements: any[];
    className?: string;
    isSubSection?: boolean;
    showToggle?: boolean;
    prefix?: string;
    calculateTotal?: boolean;
    totalFields?: string[];
