              {showToggle && (
                <Button
                  variant="ghost"
                  size="sm"
                  className="h-6 w-6 p-0 hover:bg-muted/80"
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleSection(namespacedKey);
                  }}
                  data-testid={`toggle-${sectionKey}`}
                  aria-label={`${collapsed ? 'Expand' : 'Collapse'} ${title} section`}
